opam.opam:
opam-version: "2.0"
name: "ppx_tools_versioned"
version: "5.2.1"
synopsis: "A variant of ppx_tools based on ocaml-migrate-parsetree"
maintainer: "frederic.bour@lakaban.net"
authors: [
  "Frédéric Bour <frederic.bour@lakaban.net>"
  "Alain Frisch <alain.frisch@lexifi.com>"
]
license: "MIT"
tags: "syntax"
homepage: "https://github.com/let-def/ppx_tools_versioned"
bug-reports: "https://github.com/let-def/ppx_tools_versioned/issues"
depends: [
  "ocaml" {>= "4.02.0"}
  "jbuilder" {build & >= "1.0+beta17"}
  "ocaml-migrate-parsetree" {>= "1.0.10"}
]
build: [
  ["jbuilder" "subst" "-p" name] {pinned}
  ["jbuilder" "build" "-p" name "-j" jobs]
  ["jbuilder" "runtest" "-p" name "-j" jobs] {with-test}
]
dev-repo: "git://github.com/let-def/ppx_tools_versioned.git"
url {
  src:
    "https://github.com/ocaml-ppx/ppx_tools_versioned/archive/5.2.1.tar.gz"
  checksum: "md5=1ae6ae43ec161fbbf12c2b4d3a7e26f5"
}

opam-version: "2.0"
name: "gen"
version: "0.5.1"
synopsis: "Simple and efficient iterators (modules Gen and GenLabels)."
description: """
Now provides additional modules GenClone and GenMList for lower-level control
over persistency and duplication of iterators."""
maintainer: "simon.cruanes.2007@m4x.org"
authors: "Simon Cruanes"
tags: ["gen" "iterator" "iter" "fold"]
homepage: "https://github.com/c-cube/gen/"
doc: "http://cedeela.fr/~simon/software/gen/"
bug-reports: "https://github.com/c-cube/gen/issues"
depends: [
  "ocaml"
  "jbuilder" {build}
  "base-bytes"
  "odoc" {with-doc}
  "qtest" {with-test}
  "qcheck" {with-test}
]
build: [
  ["jbuilder" "build" "-p" name]
  ["jbuilder" "runtest"] {with-test}
  ["jbuilder" "build" "@doc"] {with-doc}
]
dev-repo: "git+https://github.com/c-cube/gen.git"
url {
  src: "https://github.com/c-cube/gen/archive/0.5.1.tar.gz"
  checksum: "md5=4fb545ddde26dd084f01210681df4c14"
}

